const hAcco = document.querySelector('menu');
const hAccoItems = document.querySelectorAll('.menu-acco__item');
const closeItem = function(item) {
    item.classList.remove('menu-acco__item--active');
}
const openItem = function(item) {
    preventDefault('menu-acco__trigger');
    for ( let i = 0; i < hAccoItems.length; i++) {
        hAccoItems[i].classList.remove('menu-acco__item--active');
            
    } 
    item.classList.add('.menu-acco__item--active');
}

hAcco.addEventListener('click', function(event){
    event.preventDefault('menu-acco__trigger');
    const target = event.target;
    const targetItem = target.parentNode
    if (target.classList.contains('menu-acco__trigger')) {
        if (targetItem.classList.contains('menu-acco__item--active')) {
            closeItem(targetItem);
         } else {
                openItem(targetItem);
                
         }
    }
        if (target.closest('menu') === null ) {
            for ( let i = 0; i < hAccoItems.length; i++) {
                hAccoItems[i].classList.remove('menu-acco__item--active');
                    
            } 
        }
})